mapper.insert:

insert into commodityimage (commodityImgId, commodityId,
commodityImgName,
commodityImgDate, version, commodityImg
)
values
(#{commodityimgid,jdbcType=BIGINT}, #{commodityid,jdbcType=BIGINT},
#{commodityimgname,jdbcType=VARCHAR},
#{commodityimgdate,jdbcType=VARCHAR}, #{version,jdbcType=BIGINT},
#{commodityimg,jdbcType=LONGVARBINARY}
)

mapper.select:
select commodityImgId
from commodityimage where
commodityId=#{commodityid, jdbcType=BIGINT}
